import{_ as a,o as s,c as n,a as o}from"./app.2f199172.js";const y=JSON.parse('{"title":"Installation","description":"","frontmatter":{},"headers":[{"level":2,"title":"Manual","slug":"manual"},{"level":2,"title":"Uninstallation","slug":"uninstallation"}],"relativePath":"installation.md"}'),e={name:"installation.md"},l=o(`<h1 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-hidden="true">#</a></h1><p>Open Beta 1 is only supported on macOS and Linux systems.<br> Just copy and run the installation script in your terminal:</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">bash -c </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">curl -sSfL https://raw.githubusercontent.com/graph-guard/ggproxy-releases/main/install.sh | sudo sh -s -- 0.2.0</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span></code></pre></div><p>Once you&#39;ve installed ggproxy you need to configure your setup under <code>/etc/ggproxy</code>.<br><a href="/configuration">Read more about how to configure your ggproxy server</a>.</p><p>If the installation script fails, please consider the manual installation instructions below.</p><h2 id="manual" tabindex="-1">Manual <a class="header-anchor" href="#manual" aria-hidden="true">#</a></h2><ol><li>Download the <a href="https://github.com/graph-guard/ggproxy-releases/releases" target="_blank" rel="noreferrer">Beta release</a> archive containing the executable targeting your operating system.</li></ol><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#82AAFF;">cd</span><span style="color:#A6ACCD;"> /tmp </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> curl -LO https://github.com/graph-guard/ggproxy-releases/releases/download/0.2.0/ggproxy-0.2.0-linux-amd64.tar.gz</span></span>
<span class="line"></span></code></pre></div><ol start="2"><li>Extract the archive:</li></ol><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">mkdir ggproxy </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> tar -xvf ggproxy-0.2.0-linux-amd64.tar.gz -C ggproxy </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">cd</span><span style="color:#A6ACCD;"> ggproxy</span></span>
<span class="line"></span></code></pre></div><ol start="3"><li>Move your executable to <code>/usr/local/bin/</code> and the config directories to <code>/etc/ggproxy/</code>:</li></ol><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">sudo mv usr/local/bin/ggproxy /usr/local/bin/</span></span>
<span class="line"><span style="color:#A6ACCD;">sudo mv etc/ggproxy /etc/</span></span>
<span class="line"></span></code></pre></div><ol start="4"><li>Change permissions:</li></ol><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">sudo chmod +x /usr/local/bin/ggproxy</span></span>
<span class="line"><span style="color:#A6ACCD;">sudo find /etc/ggproxy/ -type d -exec chmod 775 -- {} +</span></span>
<span class="line"><span style="color:#A6ACCD;">sudo find /etc/ggproxy/ -type f -exec chmod 664 -- {} +</span></span>
<span class="line"></span></code></pre></div><ol start="5"><li>Setup your configuration in <code>/etc/ggproxy</code><br><a href="/configuration">Read more about how to configure your ggproxy server</a>.</li></ol><h2 id="uninstallation" tabindex="-1">Uninstallation <a class="header-anchor" href="#uninstallation" aria-hidden="true">#</a></h2><p>To uninstall <code>ggproxy</code> just copy and run the installation script in your terminal:</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">bash -c </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">curl -sSfL https://raw.githubusercontent.com/graph-guard/ggproxy-releases/main/install.sh | sudo sh -s -- -r</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span></code></pre></div><p>If the uninstallation script fails, please consider the manual uninstallation instructions below:</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">sudo rm -rf /usr/local/bin/ggproxy /etc/ggproxy</span></span>
<span class="line"></span></code></pre></div>`,20),t=[l];function p(r,c,i,u,d,g){return s(),n("div",null,t)}const b=a(e,[["render",p]]);export{y as __pageData,b as default};
